class DatabaseService {
	constructor({ databaseRepository }) {
		this.databaseRepository = databaseRepository
	}

	async findById(itemId){
		return this.databaseRepository.findById(itemId)
	}

	async create(data){
		return this.databaseRepository.create(data)
	}

	async delete(itemId){
		return this.databaseRepository.delete(itemId)
	}

	async update(data){
		return this.databaseRepository.update(data)
	}

	async reset(){
		return this.databaseRepository.reset()
	}

}

module.exports = DatabaseService