// Import.
const jcopy = require('jcopy');

/**
 * Record.
 */
class Record {
  /**
   * Record constructor.
   * @param {any} [data] - Data object.
   */
  constructor(data = null) {
    // Save data.
    this.timestamp = Date.now();
    this.data = jcopy(data);
  }
}

// Export.
module.exports = Record;
